mapper.insert:
insert into FINDANIMALBOARD(MNUMBER, SPECIES, KIND, LOCATION, TITLE, CONTENT

    ,IMGPATH

)
values (#{mNumber}, #{species}, #{kind}, #{location}, #{title}, #{content}


    ,#{imgPath}

)

mapper.update:
update FINDANIMALBOARD set SPECIES = #{species}, KIND = #{kind}, LOCATION = #{location},
IMGPATH = #{imgPath},
TITLE = #{title}, CONTENT = #{content} where FANUMBER = #{faNumber}

mapper.select:
select count(*) from FINDANIMALBOARD
and SPECIES = #{species}
and AnimalState = #{animalState}
and ( TITLE like '%' || #{keyword} || '%' or CONTENT like '%' || #{keyword} || '%' or KIND like '%' || #{keyword} || '%' or LOCATION like '%' || #{keyword} || '%')

select * from (select ROWNUM as NUM, A.* FROM (select * from FINDANIMALBOARD
and SPECIES = #{species}
and AnimalState = #{animalState}
and ( TITLE like '%' || #{keyword} || '%' or CONTENT like '%' || #{keyword} || '%' or KIND like '%' || #{keyword} || '%' or LOCATION like '%' || #{keyword} || '%')
order by FANUMBER DESC) A) where NUM between #{start} and #{end}

