mapper.update:
update MEMBER
set PW = #{pw}, NAME = #{name}, EMAIL = #{email}, PHONE = #{phone}
where MNUMBER = #{mNumber}

mapper.select:
SELECT MNUMBER
from MEMBER
where ID = #{id} and PW = #{pw}

select NAME
from MEMBER
where MNUMBER = #{pk}

select BIRTH
from MEMBER
where MNUMBER = #{pk}

select PHONE
from MEMBER
where MNUMBER = #{pk}

select EMAIL
from MEMBER
where MNUMBER = #{pk}

select ID
from MEMBER
where MNUMBER = #{pk}
